mapper.select:
SELECT  from  ql_goods a
left join ql_dict b on b.type='hv_type' and b.label='now_rate_doc'
left join ql_dict d on d.type='hv_type' and d.label='now_rate_dlm'
left join company_country com on com.good_id = a.id
left join ql_companys cp on a.company_id = cp.id
where 1=1

	and a.is_group = #{isGroup}


	and a.is_flash_sale = #{isFlashSale}


	and com.country_id=#{addressId}


	and a.company_id = #{companyId}


	and a.id = #{goodsId}

SELECT  from  ql_goods a
left join ql_dict b on b.type='hv_type' and b.label='now_rate_doc'
left join ql_dict d on d.type='hv_type' and d.label='now_rate_dlm'
left join company_country com on com.good_id = a.id
left join ql_companys cp on a.company_id = cp.id
where 1=1

	and a.is_building = #{isBuilding}


	and a.is_market = #{isMarket}


	and a.is_group = #{isGroup}


	and a.is_flash_sale = #{isFlashSale}


	and a.is_index_shop = #{isIndexShop}


	and a.is_index_market = #{isIndexMarket}


	and a.is_index_build = #{isIndexBuild}


	and com.country_id=#{addressId}


	and a.company_id = #{companyId}


	ORDER BY a.MODIFYTIME


	ORDER BY a.sys_update_date


	ORDER BY a.QUERY_NUM


	limit 0, #{limitNum,jdbcType=INTEGER}

