mapper.select:
	select ROWNUM, ID,TITLE,TYPE_ID,CREATETIME,STATUS from QL_SUPPLY where DISABLED='false'

   	and STATUS = #{status,jdbcType=VARCHAR}

	and ROWNUM <=11 ORDER BY CREATETIME DESC

     select * from (select * from (
		select ROWNUM AS RN,t.* from (
	select  ID,TITLE,CREATETIME,TYPE_ID,TYPE_NAME,STATUS,VIEWNUM from QL_SUPPLY 
    where USER_ID = #{userId,jdbcType=VARCHAR}  and DISABLED='false' and TYPE_NAME= #{supplyType,jdbcType=VARCHAR}
    ) t
		) where RN <= #{page}*#{rows})
		where RN>(#{page}-1)*#{rows}

	 select count("ID")
  from QL_SUPPLY
  where USER_ID = #{userId,jdbcType=VARCHAR} and　DISABLED='false' and TYPE_NAME= #{supplyType,jdbcType=VARCHAR}

  	select * from (select * from (
		select ROWNUM AS RN,t.* from (
	  		select ID,TITLE,TYPE_ID,DESCRIPTION,LINKMAN,LINKMOBILE,PICPATH,CREATETIME,EFFECTIVETIME FROM QL_SUPPLY
	  		WHERE DISABLED='false'

       	  		and TYPE_ID = #{typeId,jdbcType=VARCHAR}


	       		and STATUS = #{status,jdbcType=VARCHAR}

	      	ORDER BY CREATETIME DESC
      	) t
	) where RN <= #{page}*#{rows})
	where RN>(#{page}-1)*#{rows}

 	select COUNT("ID") FROM QL_SUPPLY
		WHERE DISABLED='false'

      	  	and TYPE_ID = #{typeId,jdbcType=VARCHAR}


      		and STATUS = #{status,jdbcType=VARCHAR}

 	select COUNT("ID") FROM QL_SUPPLY
		WHERE DISABLED='false'
      	  	and TYPE_NAME = #{typeName}
      		 and USER_ID = #{userId}
